function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".left__item"),t=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active"),t.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active"),t.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>1149.98&&(s.classList.remove("active"),e.classList.remove("active"),t.classList.remove("locked"))}))}burgerMenu();