function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".left__item"),s=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active"),s.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active"),s.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>1149.98&&(t.classList.remove("active"),e.classList.remove("active"),s.classList.remove("locked"))}))}burgerMenu();const elemsAnim=document.querySelectorAll(".dashboard__item"),laptopScreen=window.matchMedia("(max-width: 1150px");if(!0==!laptopScreen.matches)elemsAnim.forEach(((e,t)=>{setTimeout((()=>{e.classList.add("show")}),600*t)}));else{const e=document.querySelector(".right__item"),t=document.querySelector(".left__item");e.classList.add("mobile"),t.style.opacity=1}